declare(strict_types=1);

namespace Mirasvit\Assistant\Ui\Prompt\Form\Control;

use Mirasvit\Assistant\Api\Data\CriterionInterface;
use Mirasvit\Assistant\Api\Data\PromptInterface;

class DeleteButton extends ButtonAbstract
{

    public function getButtonData(): array
    {
        $data = [];
        if ($this->getId()) {
            $data = [
                'label'      => __('Delete'),
                'class'      => 'delete',
                'on_click'   => 'deleteConfirm(\'' . __(
                    'Are you sure you want to do this?'
                ) . '\', \'' . $this->getDeleteUrl() . '\')',
                'sort_order' => 20,
            ];
        }

        return $data;
    }

    public function getDeleteUrl(): string
    {
        return $this->getUrl('*/*/delete', [PromptInterface::ID => $this->getId()]);
    }
}
